import '../assets/styles/Pages.css';

function About() {
  return (
    <section className="page about-page">
      <h2 className="page-title">About Uchit The Luxury Spa</h2>
      <p>
        At Uchit The Luxury Spa, we offer a serene environment where you can unwind and rejuvenate. Our experienced staff from the North East and local regions provide a variety of massage therapies tailored to your needs.
      </p>
      <p>
        Our facility includes showers and comfortable rooms to ensure a relaxing experience. Visit us at Bhatkhedi Chourha, Mhow Pithampur Road, opposite BS Hospital.
      </p>
    </section>
  );
}

export default About;
