import React, { useState, useEffect } from "react";
import '../assets/styles/Home.css';

const Home = () => {
  // Image Carousel State
  const [currentImage, setCurrentImage] = useState(0);
  const carouselImages = [
    "/images/a.jpg",
    "/images/b.jpg",
    "/images/c.jp"
  ];

  // Auto slide functionality
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % carouselImages.length);
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval); // Clean up on unmount
  }, []);

  const handleCarouselClick = (index) => {
    setCurrentImage(index);
  };

  // Services Section
  const services = [
    { name: "Dry Massage", price: "1500" },
    { name: "Aroma Oil Massage", price: "2000" },
    { name: "Cream Massage", price: "2500" },
    { name: "Mintjel Massage", price: "2500" },
    { name: "Chocolate Massage", price: "3000" },
    { name: "Deep Tissue Massage", price: "3000" },
    { name: "Thai Massage", price: "4000" },
    { name: "Hand Massage", price: "4000" }
  ];

  // About Us Section
  const aboutUsContent = `
    Midnight Spa is your ultimate destination for relaxation and rejuvenation. 
    Offering various soothing therapies including Dry Massage, Aroma Oil Massage, 
    and more, our spa ensures you leave feeling refreshed and revitalized.
  `;

  return (
    <div className="home">
      {/* Header */}
      <header className="header">
        <div className="logo">
          <h1>Midnight Spa ✨</h1>
        </div>
        <div className="header-info">
          <p className="special-offer">
            Special Offer Only ₹1199/- ✨ (30+15 mins - 45 mins)
          </p>
          <p>👩 STAFF: North East & Local 👩</p>
          <p>Location: Bhatkhedi Chourha, Mhow Pithampur Road, Opp BS Hospital</p>
          <p>Contact No: 📵 8878873358, 8871216273</p>
        </div>
      </header>

      {/* Image Carousel (Full-width with time and clickable slide) */}
      <div className="carousel">
        <img
          src={carouselImages[currentImage]}
          alt={`carousel-img-${currentImage}`}
          className="carousel-image"
        />
        <div className="carousel-nav">
          {carouselImages.map((img, index) => (
            <button
              key={index}
              className={`carousel-btn ${currentImage === index ? "active" : ""}`}
              onClick={() => handleCarouselClick(index)}
            ></button>
          ))}
        </div>
      </div>

      {/* Services */}
      <section className="services">
        <h2 className="section-title">✨ Types of Massage</h2>
        <ul className="service-list">
          {services.map((service, index) => (
            <li key={index} className="service-item">
              <span>{service.name}</span>
              <span>₹ {service.price}</span>
            </li>
          ))}
        </ul>
      </section>

      {/* Offers */}
      <section className="offers">
        <h2 className="section-title">💫 Special Offers</h2>
        <p className="offer-description">Exclusive packages starting from ₹1199!</p>
      </section>

      {/* About Us */}
      <section className="about">
        <h2 className="section-title">About Us</h2>
        <p>{aboutUsContent}</p>
      </section>

      {/* Image Carousel (3 images per row with time & click) */}
      <div className="three-image-carousel">
        <div className="carousel-row">
          <img
            src="/images/slider1.jpg"
            alt="slider-img1"
            className="carousel-image"
          />
          <img
            src="/images/slider2.jpg"
            alt="slider-img2"
            className="carousel-image"
          />
          <img
            src="/images/slider3.jpg"
            alt="slider-img3"
            className="carousel-image"
          />
        </div>
      </div>

      {/* Contact Us */}
      <section className="contact">
        <h2 className="section-title">📞 Contact Us</h2>
        <p>Have questions? We’d love to hear from you!</p>
        <form>
          <input type="text" placeholder="Your Name" className="contact-input" />
          <input type="email" placeholder="Your Email" className="contact-input" />
          <textarea
            placeholder="Your Message"
            className="contact-input"
          ></textarea>
          <button type="submit" className="contact-btn">
            Send Message
          </button>
        </form>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>© 2025 Midnight Spa - All Rights Reserved</p>
      </footer>
    </div>
  );
};

export default Home;
